    public const DEFINITION = ['_GeneticObservationInterpretation' => 'Kode untuk interpretasi terkait analisis genetik seperti “positif”,“negatif’, “karier”, “responsif”, dll.', 'CAR' => 'Pasien dianggap sebagai carrier berdasarkan hasil pemeriksaan. Carrier adalah seorang individu yang membawa suatu bentuk gen yang berubah yang dapat menyebabkan keturunannya memiliki penyakit genetik.', '_ObservationInterpretationChange' => 'Interpretasi perubahan terkait kuantitas dan/atau tingkat keparahan (severity). Maksimal satu dari B atau W dan satu dariU atau D diperbolehkan', 'B' => 'Hasil observasi saat ini telah meningkat dibandingkan dengan hasil observasi sebelumnya. [Catatan: dapat diterapkan pada observasikuantitatif atau kualitatif.]', 'D' => 'Hasil observasi saat ini menurun dari hasil sebelumnya untuk pengamatan kuantitatif.', 'U' => 'Hasil saat ini meningkat dari hasil sebelumnya untuk pengamatan kuantitatif.', 'W' => 'Hasil observasi saat ini menurun dibandingkan dengan hasil observasi sebelumnya. [Catatan: dapat diterapkan pada observasi kuantitatif atau kualitatif.]', '_ObservationInterpretationExceptions' => 'Pengecualian teknis yang mengakibatkan ketidakmampuan untuk memberikan interpretasi. Maksimal satu diperbolehkan. Tidak menyatakan normalitas atau keparahan.', '<' => 'Hasilnya di bawah batas deteksi minimum (prosedur pengujian atau peralatan adalah faktor pembatas). Sinonim: below analytical limit, low off scale.', '>' => 'Hasilnya di atas batas terukur maksimum (prosedur pengujian atau peralatan adalah faktorpembatas). Sinonim: Aboveanalytical limit, high off scale', 'IE' => 'Tidak ada cukup bukti bahwa spesies yang dimaksud adalah target yang baik untuk terapi dengan obat. Sebuah interpretasi kategoris tidak memungkinkan. [Catatan: Minimum InhibitoryConcentration (MIC) dengan "IE" dan/atau catatan tambahan dapat dilaporkan (tanpa disertai kategorisasi S, I, atau R).]', '_ObservationInterpretationNormality' => 'Interpretasi normalitas atau tingkat kelainan (termasuk tingkat kritis atau "waspada"). Paling banyak satu interpretasi diperbolehkan.', 'A' => 'Hasil observasi berada di luar nilai normal [Catatan: Biasanya berlaku untuk hasil non-numerik.]', 'AA' => 'Hasil observasi berada di luar nilai normal dan memerlukan tindakan segera untuk keselamatan pasien. [Catatan: Biasanya berlaku untuk hasil non-numerik. Analog denganbatas kritis untuk hasil numerik.]', 'HH' => 'Hasil observasi kuantitatif berada di atas nilai normal dimana tindakan segera harus dipertimbangkan untuk keselamatan pasien.', 'LL' => 'Hasil observasi kuantitatif berada di bawah nilai normal dimana tindakan segera harus dipertimbangkan untuk keselamatan pasien.', 'H' => 'Hasil observasi kuantitatif berada di atas batas atas nilai normal', 'HU' => 'Hasil observasi kuantitatif yang secara signifikan lebih tinggi dari nilai normal atau interval terapeutik, tetapi belum mencapai nilai kritis yang tinggi dan mungkin memerlukan perhatian khusus.', 'L' => 'Hasil observasi kuantitatif berada di bawah batas bawah nilai normal.', 'LU' => 'Hasil observasi kuantitatif yang secara signifikan lebih rendah dari nilai normal atau interval terapeutik, tetapi belum mencapai nilai kritis yang rendah dan mungkin memerlukan perhatian khusus', 'N' => 'Hasil observasi dalam rentang nilai normal [Catatan: Berlaku untuk hasil numerik ataunon-numerik.]', '_ObservationInterpretationSusceptibility' => 'Interpretasi hasil uji kepekaan anti-mikroba (mikrobiologi).Maksimal satu diperbolehkan.', 'I' => 'Strain bakteri dihambat secara in vitro oleh konsentrasi agen antimikroba yang dikaitkan dengan efek terapeutik yang tidak pasti. Tipe ini menunjukkan bahwa infeksi dapat disembuhkan pada lokasi tubuh dimana obat secara fisiologis terkonsentrasi atau menggunakan dosis tinggi dari obat tersebut.', 'NCL' => 'Digunakan ketika tidak cukup data uji klinis yang diterbitkan oleh Clinical and Laboratory Standards Institutes (CLSI) untuk menetapkan titik susceptible/intermediate/resistant.', 'NS' => 'Kategori yang digunakan untuk isolat yang hanya memiliki kriteria susceptible karena kejadian resistensi tidak ada atau jarang. Isolat yang memiliki MIC diatas atau diameter zona dibawah nilai yang diindikasikan untuk susceptible, maka dilaporkan sebagai non-susceptible.Sinonim: decreased susceptibility.', 'R' => 'Strain bakteri dihambat secara in vitro oleh konsentrasi agen antimikroba yang dikaitkan dengan kemungkinan kegagalan terapi yang tinggi.', 'SYN-R' => 'Kategori untuk isolat dimana bakteri resisten terhadap kombinasi terapi (contoh. Kombinasi penisilin dangentamisin)', 'S' => 'Strain bakteri yang dihambat oleh konsentrasi agen antimikroba in vitro yang dikaitkan dengan kemungkinan keberhasilan terapi yang tinggi. Sinonim : sensitive', 'SDD' => 'A category that includes isolates with antimicrobial agent minimum inhibitory concentrations (MICs) that approach usually attainable blood "and tissue levels and for which response rates may be lower than for susceptible isolates. Reference:CLSI document M44-A2 2009 ""Method for antifungal disk diffusion susceptibility testing of yeasts; approved guideline second edition"" - page 2."', 'SYN-S' => 'Kategori untuk isolat dimana bakteri sensitif terhadap kombinasi terapi (contoh. Kombinasi penisilin dangentamisin)', 'EX' => 'Hasil observasi diinterpretasikandiluar rentang inklusi untukprotokol tertentu', 'HX' => 'Hasil observasi diinterpretasikan diatas rentang inklusi untukprotokol tertentu', 'LX' => 'Hasil observasi diinterpretasikan dibawah rentang inklusi untuk protokol tertentu.', 'ObservationInterpretationDetection' => 'Interpretasi ada atau tidak adanya komponen / analit atau organisme dalam tes atau tanda dalam pengamatan klinis. Sesuai dengan praktik pengolahan data laboratorium, konsep-konsep ini memberikan interpretasi kategoris tentang "makna" nilai kuantitatif untuk pengamatan yang sama.', 'IND' => 'Komponen/analit, organisme atau tanda klinis yang ditentukan tidak dapat dinyatakan positif / negatif atau terdeteksi / tidak terdeteksi oleh tes atau prosedur yang dilakukan.', 'E' => 'Tes atau prosedur berhasildilakukan, tetapi hasilnya adalah borderline dan tidak dapat dinyatakan positif / negatif atau terdeteksi / tidak terdeteksi sesuai dengan kriteria yang ditetapkan saat ini.', 'NEG' => 'Tidak ditemukannya komponen / analit tertentu, organisme atau tanda klinis berdasarkan ambang batas yang ditetapkan dari tes atau prosedur yang dilakukan. [Catatan: Negatif tidak selalu berarti tidak adanya item yangditentukan.]', 'ND' => 'Kehadiran komponen / analit tertentu, organisme atau tanda klinis tidak dapat ditentukan dalam batas deteksi tes atau prosedur yang dilakukan.', 'POS' => 'Adanya komponen / analit, organisme atau tanda klinis tertentu berdasarkan ambang batas yang ditetapkan dari tes atau prosedur yang dilakukan.', 'DET' => 'Pengukuran komponen / analit tertentu, organisme atau tanda klinis di atas batas deteksi dari tes atau prosedur yang dilakukan.', 'ObservationInterpretationExpectation' => 'Interpretasi hasil yang diamati dengan mempertimbangkan informasi tambahan(kontraindikasi) tentang situasi pasien. Paling banyak satu kode diperbolehkan.', 'EXP' => 'Hasil telah dievaluasi berdasarkan kontraindikasi yang diketahui. Setelah kontraindikasi tersebut diperhitungkan, hasilnya ditentukan sebagai "expected" (misalnya, adanya penggunaan obat pada pasien yang mengkonsumsi obat yang diresepkan untuk manajemen nyeri).', 'UNE' => 'Hasil telah dievaluasi berdasarkan kontraindikasi yang diketahui. Setelah kontraindikasi tersebut diperhitungkan, hasilnya ditentukan sebagai "Unexpected" (misalnya, adanya obat yang tidak diresepkan pada pasien yang mengkonsumsi obat resep untuk manajemen nyeri)', 'ReactivityObservationInterpretation' => 'Interpretasi keberadaan dan tingkat reaktivitas komponen / analit yang ditentukan dengan reagen dalam uji laboratorium yang dilakukan.', 'NR' => 'Menunjukkan bahwa komponen / analit yang ditentukan tidak bereaksi secara terukur dengan reagen (absence finding).', 'RR' => 'Menunjukkan bahwa komponen / analit tertentu bereaksi dengan reagen di atas batas terukur dari pengujian yang dilakukan(presence finding).', 'WR' => 'Menunjukkan bahwa komponen / analit tertentu bereaksi dengan reagen, namun di bawah batas terukur dari pengujian yang dilakukan (presence finding).', 'OI000001' => 'null', 'OI000002' => 'null', 'OI000003' => 'null', 'OI000004' => 'null', 'OI000005' => 'null', 'OI000006' => 'null', 'OI000007' => 'null', 'OI000008' => 'null', 'OI000009' => 'null', 'OI000010' => 'null', 'OI000011' => 'null', 'OI000012' => 'null', 'OI000013' => 'null', 'OI000014' => 'null', 'OI000015' => 'null', 'OI000016' => 'null', 'OI000017' => 'null', 'OI000019' => 'Tidak ada sakit hingga sakit ringan', 'OI000020' => 'Sakit ringan hingga sakit sedang', 'OI000021' => 'null', 'OI000022' => 'null', 'OI000023' => 'null', 'OI000024' => 'null', 'OI000026' => 'null', 'OI000027' => 'null', 'OI000028' => 'null'];


